;funcoes menu
;====================================================================================================================
lerInputMenu:
    cmp al, 'w'
    je W
    cmp al, 's'
    je S
    cmp al, 32
    je selecionado
    jmp start

W:
    push ax  ;Salva os registradores
    push dx
    xor ax, ax  ;Zera os registradores
    mov dx, ax

    mov si, op_menu ;carega valor de op_menu em al
    lodsb
    dec si

    dec al
    cmp al, 0
    jne .salva
    mov al, 1

    .salva:
        mov di, si   ;atualiza o valor de op_menu
        stosb

        pop ax   ;restaura registradores
        pop dx
        jmp start

S:
    push ax  ;Salva os registradores
    push dx
    xor ax, ax  ;Zera os registradores
    mov dx, ax

    mov si, op_menu ;carega valor de op_menu em al
    lodsb
    dec si

    inc al
    cmp al, 3
    jne .salva
    mov al, 2

    .salva:
        mov di, si   ;atualiza o valor de op_menu
        stosb

        pop ax   ;restaura registradores
        pop dx
        jmp start
selecionado:
    push ax  ;Salva os registradores
    push dx
    xor ax, ax  ;Zera os registradores
    mov dx, ax

    mov ah, 0           ;modo video   
    mov al, 13h           
    int 10h

    mov si, op_menu ;carega valor de op_menu em al
    lodsb

    cmp al, 1
    je set_lateral

    cmp al, 2
    je instrucoes
    jmp start


