_print_pixel:    ;imprime um pixel
    push bx
    mov ah, 0ch
    mov bh, al
    int 10h
    pop bx
    ret

_prtln:  ;imprime uma linha na tela
    push ax
    mov al, 50

    .loop1:
        dec al
        push ax
        lodsb
        call _print_pixel
        pop ax
        inc cx
        cmp al, 0
        je .fim
        jmp .loop1

        .fim:
            pop ax
            ret

_prtimg:
    push ax
    mov al, 50
    .loop2:
        mov cx, bx
        dec al
        call _prtln
        inc dx
        cmp al, 0
        je .fim
        jmp .loop2

        .fim:
            pop ax
            ret
            

lerInputJogo:
    cmp al, 'a'
    je A
    cmp al, 'd'
    je D
    jmp jogo

A:
    push ax  ;Salva os registradores
    push dx
    xor ax, ax  ;Zera os registradores
