check_obs:
    push ax   ;salva registradores
    push si
    xor ax, ax

    mov si, o_posy
    lodsb
    cmp al, 160
    je .reset
    pop si
    pop ax
    ret


    .reset:
        mov di, o_posy ;reseta a altura do obstaculo
        mov al, 0
        stosb

        ;troca a posicao em x
        mov si, o_posx
        lodsb
        cmp ax, 100
        je .troca
        mov di, o_posx
        mov ax, 100
        stosb
        jmp .end

        .troca:
            mov di, o_posx
            mov ax, 150
            stosb

        .end:
        pop si
        pop ax

    ret

animar:
    push si  ;salva registradores
    push ax
    push dx
    push bx
    push cx
    xor ax, ax
    mov dx, ax
    mov cx, ax
    mov bx, ax

    mov si, o_posy
    lodsb
    mov dx, ax
    add ax, 20
    mov di, o_posy
    stosb
    mov si, o_posx
    lodsb
    mov cx, ax
    mov bx, ax
    mov si, ferrari
    call _prtimg
    

    pop cx
    pop bx
    pop dx
    pop ax
    pop si

    ret

colisao:
    push ax
    push bx
    push si
    xor ax, ax
    xor bx, bx

    mov si, p_pos ;carrega p_pos em bx
    lodsb
    mov bx, ax

    xor ax, ax
    mov si, o_posx  ;carrega o_posx em al
    lodsb

    cmp bx, 0
    je .esq
    jmp .dir

    .esq:
        cmp ax, 100
        je .altura
        jmp .end

    .dir:
        cmp ax, 150
        je .altura
        jmp .end

    .altura:
        xor ax, ax
        mov si, o_posy  ;carrega o_posy em al
        lodsb
        cmp ax, 110
        ja game_over
        jmp .end

    .end:
        pop si
        pop bx
        pop ax
        ret

