start:
    xor ax, ax  ;zera registradores
    mov ds, ax
    mov es, ax
    mov cx, ax

    mov ah, 0
    mov al, 13h
    int 10h  ;inicia modo de video

    call printTitle
    call printNome1
    call printNome2
    call printNome3
    
    mov si, op_menu
    lodsb   ;carrega valor de op_menu em al
    dec si
    
    ;verifica a opcao selecionada
    cmp al, 1
    je .op1
    cmp al, 2
    je .op2

    .op1:
        call printOp1s
        call printOp2
        jmp .continue
    
    .op2:
        call printOp1
        call printOp2s
        jmp .continue

    .continue:
        call getchar
        call lerInputMenu

    jmp start

